import './write.css'
import { useState,useContext } from 'react'
import axios from 'axios'
import { Context } from '../../context/Context'
import browserHistory from '../../history/history'

export default function Write() {
    const [title, setTitle] = useState('')
    const [desc, setDesc] = useState('')
    const [file, setFile] = useState(null)
    const {user} = useContext(Context)

    function getRandomInt(max) {
        return Math.floor(Math.random() * max);
      }

    const handleSubmit = async (e)=>{
        e.preventDefault();
        const newPost = {
            username: user.username,
            title,
            desc
        }
        if(file){
            const data =new FormData();
            const filename = Date.now() + file.name;
            data.append('name', filename);
            data.append('file', file);
            newPost.photo = filename;
            try{
                await axios.post('/upload', data);
            }catch(err){

            }
        }else{
            const photos = ['1625881510282book-with-green-board-background.jpg',
                '1625881815139book-with-green-board-background.jpg',
                '1625881847297back-school-background-with-school-supplies-chalkboard.jpg',
                '16259025383721440x900_791d4a9bd6cc529.jpeg',
                '162590931057220190928013027762.jpeg',
                '162590961528320.jpg',
                '162590973708521.jpg',
                '162597197487624.jpg',
                '162597197618724.jpg',
                '162597213410824.jpg',
                '162597213527724.jpg',
                '162598695236422.jpg',
                '162598848877521.jpg',
                '162599120792120.jpg',
                '1626054591670focus-647c57d2effb7d2dfb5871161afab3cf097de6339c02e85d84ea14747800fcb0.png',
                '16260554820934bb3e43f66b5a919fea1a9005be11b86.jpeg',]
            newPost.photo = photos[getRandomInt(photos.length)]||'1625881510282book-with-green-board-background.jpg'
            console.log(newPost.photo)
        }

        try {
           const res = await axios.post("/posts", newPost);
           console.log(res.data)
        //    window.location.replace('/post/'+ res.data._id);
           browserHistory.replace({pathname: '/post/'+ res.data._id});
           browserHistory.go(0);
        } catch (error) {

        }
    }

    return (
        <div className='write'>
            {file&&<img src={URL.createObjectURL(file)} alt="" className="writeImg" />}
            <form className="writeForm" onSubmit={handleSubmit}>
                <div className="writeFormGroup">
                    <label htmlFor="fileInput"><i className="writeIcon fas fa-plus"></i></label>
                    <input type="file" id='fileInput' style={{display:'none'}} onChange={(e)=>{setFile(e.target.files[0])}}/>
                    <input type="text" placeholder='Title' className='writeInput' autoFocus={true} onChange={e=>setTitle(e.target.value)}/>
                </div>
                <div className="writeFormGroup">
                    <textarea placeholder='Tell your story...' type='text' className='writeInput writeText' onChange={e=>setDesc(e.target.value)}></textarea>
                </div>
                <button className="writeSubmit" type='submit'>Publish</button>
            </form>
        </div>
    )
}
